import React,{useState} from 'react'
import TextField from '@material-ui/core/TextField';
import { Grid,Button,ButtonGroup } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import {useNavigate} from 'react-router-dom';


const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(2),
         width: '100%',
    }
    },
  }));


const AddUser = () => {
    const classes = useStyles();
    const [state,setState] = useState({
        firstname:"",
        email:"",
        gender:"female",
    });

    const{firstname,email,gender}=state;

    const navigate = useNavigate();

    const handleChange = (e) => {
       let{firstname,email,}=(e.target.value);
  };

    const onAdd = async (user) => {
        const req = {
          id : uuid(),
          ...user
        }
       const res = await aios.post("http://localhost:5000/users",req)
       setUsers([...users, res.data])
      }

    const onSubmit = (e) => {
        e.preventDefault() 
           if ((!firstname)|| (!email) || (!gender) ) {
             alert('Please input all input field')
             return
           } 
           onAdd({firstname,email})
           setFirstname('')
           setEmail('')
           faireRedirection()
         }

    return (
        <Grid container  sm={10} style={{display: "flex", justifyContent:"flex-start" ,margin:"0 auto"}}>
            <Grid item xs={12} sm={8} style={{padding: "0px 4%",}}>

                <h3>ADD USER</h3>

                <Button style={{marginBottom:"20px"}} onClick={()=>  navigate("/")}  variant="contained" color="primary">GO BACK</Button>

                <form onSubmit={onSubmit} className={classes.root}  noValidate autoComplete="off">                  
                    <TextField id="firstname" label="First Name"  value={firstname} onChange={handleChange} type="text" />
                    <TextField id="email" label="Email" value={email} onChange={handleChange} type="email"/>  
                    <TextField                   
                       id="standard-select-gender"
                       select
                       label="Gender"
                       value={gender}
                       onChange={handleChange}
                       SelectProps={{
                        native: true,
                      }}
                       helperText="Please select your gender"
                    >
                         <option key="male" value="male">
                        Male
                        </option>
                        <option key="female" value="female">
                        Female
                        </option>
                    </TextField>
                </form>
                <Button type='submit' style={{marginTop:"10px"}} variant="contained" color="primary">ADD USER</Button>
                <Button onClick={()=>setState("")} style={{marginTop:"10px"}} variant="contained" color="secondary">CANCEL</Button>           
            </Grid>
        </Grid>
)}

export default AddUser
