import React,{useEffect}  from 'react'
import {useSelector, useDispatch} from "react-redux"
import UserCard from './UserCard'
import axios from "axios"
import { setUsers} from '../../redux/actions/actions'
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import { Grid } from '@material-ui/core';


const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    body: {
      fontSize: 14,
    },
  }))(TableCell);

  const StyledTableRow = withStyles((theme) => ({
    root: {
      '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
      },
    },
  }))(TableRow);

  const useStyles = makeStyles({
    table: {   
    },
    tableContainer: {
    }
  });


const UserList = () => {
    const classes = useStyles();

    const users = useSelector((state) => state.allUsers.users)
    const list = users;
    const dispatch = useDispatch()

    const showUsers = async () => {
        const response = await axios.get("http://localhost:5000/users")
        .catch((err)=>{
            console.log("err", err)
        })
       dispatch(setUsers(response.data))
       console.log("data",response.data)
      };

      useEffect(() => {
        showUsers();
      }, []);


    return (
  <Grid container  sm={10} style={{display: "flex", justifyContent:"center" ,margin:"0 auto"}}>
     <Grid item xs={12} sm={12} style={{padding: "0px 4%",}}>
      <TableContainer  component={Paper}>
        <Table  aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell>User</StyledTableCell>
              <StyledTableCell align="right">Name</StyledTableCell>
              <StyledTableCell align="right">Email</StyledTableCell>
              <StyledTableCell align="right">Action</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list.map((index)=>(
              <StyledTableRow key={index.id}>
                <StyledTableCell component="th" scope="row">
                  {}
                  {console.log("dataffff",index.firstname)}
                </StyledTableCell>
                <StyledTableCell align="right">hhhhhhh</StyledTableCell>
                <StyledTableCell align="right">hhhhhhh</StyledTableCell>
                <StyledTableCell align="right">ggggggg</StyledTableCell>
              </StyledTableRow>
          ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Grid>
  </Grid>
    );
}

export default UserList
