import { ActionType } from "../constants/action_type";

const initialState ={
    users: [],
    user: {},
    loading: false,
};

export const sersReducer =(state = initialState,action) => {
    switch(action.type){
      case ActionType.SET_PRODUCTS:
        return {
                ...state,
                products: action.payload
        }
      default:
        return state;
    }
  } 


export default usersReducer
