import { ActionType } from "../constants/action_type";

const setUsers =(users) => {
    return {
      type: ActionType.SET_USERS,  
      payload: users,
    }
  }

const deleteUser =() => {
    return {
      type: ActionType.REMOVE_SELECTED_USER,  
    }
  }


export {setUsers,deleteUser};
